import threadingimport time"""leider laeuft nicht"""class myFred(threading.Thread):        def __init__(self,iD,name):        threading.Thread.__init__(self)        self.iD = iD        self.name = name    def run(selfself):        print("Starte", self.iD)        time.sleep(iD*3)        print("beende", self.iD)t1 = myFred(1 , "t1")t2 = myFred(2 , "t2")t1.start()t2.start()print('Main Fred beendet')